import React, { useState } from 'react';
import { Document, Page } from 'react-pdf';
import * as pdfjs from 'pdfjs-dist';

// Se stai utilizzando un bundle locale
import pdfjsWorker from 'pdfjs-dist/build/pdf.worker.entry';

pdfjs.GlobalWorkerOptions.workerSrc = pdfjsWorker;

const PdfViewer = ({ file }) => {
    const [numPages, setNumPages] = useState(null);
    const [pageNumber, setPageNumber] = useState(1);

    function onDocumentLoadSuccess({ numPages }) {
        setNumPages(numPages);
    }

    return (
        <div>
            <Document file={file} onLoadSuccess={onDocumentLoadSuccess}>
                <div className="flex items-center justify-between">
                    <button
                        className="bg-blue-500 text-white px-4 py-2 m-2 disabled:opacity-50"
                        onClick={() => setPageNumber(prevPageNumber => Math.max(prevPageNumber - 1, 1))}
                        disabled={pageNumber <= 1}
                    >
                        Previous
                    </button>
                    <div className="flex flex-col items-center">
                        <p>Page {pageNumber} of {numPages}</p>
                        <Page pageNumber={pageNumber} />
                    </div>
                    <button
                        className="bg-blue-500 text-white px-4 py-2 m-2 disabled:opacity-50"
                        onClick={() => setPageNumber(prevPageNumber => Math.min(prevPageNumber + 1, numPages))}
                        disabled={pageNumber >= numPages}
                    >
                        Next
                    </button>
                </div>
            </Document>
        </div>
    );
}

export default PdfViewer;
