import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import PdfViewer from './PdfViewer/pdfViewer';

const PdfDropZone = () => {
    const [isPdf, setIsPdf] = useState(false);

    // es-lint disable-next-line
    const [selectedPdf, setSelectedPdf] = useState(null);

    const onDrop = useCallback(acceptedFiles => {
        // Prendi il primo file (in base al tuo output sembra che ci sia solo un file alla volta)
        const file = acceptedFiles[0];

        if (file && file.type !== 'application/pdf') {
            setIsPdf(true);
        } else {
            setIsPdf(false);
            console.log(file);
        }
    }, []);

    const { getRootProps, getInputProps, isDragActive } = useDropzone({
        onDrop,
        accept: 'application/pdf'
    });

    return (
        <>
            <div className="flex flex-col items-center">

                <div {...getRootProps()} className="min-w-[50rem] border-dashed border-4 border-gray-500 p-10 mt-10 mx-auto w-1/2 h-60 flex flex-col justify-center items-center hover:bg-gray-100 bg-pink-200">
                    <input {...getInputProps()} />
                    {
                        isDragActive ?
                            <p>Trascina il file PDF qui...</p> :
                            <p>Trascina un file PDF qui o fai clic per selezionarlo.</p>

                    }

                </div>
                {isPdf && <p className="mt-4 text-red-600">Il file inserito non Ã¨ un PDF. Per favore inserisci un
                    file PDF.</p>}
            </div>
            <div className="bg-blue-300">
                {selectedPdf && <PdfViewer file={selectedPdf} />}
            </div>
        </>
    )
}

export default PdfDropZone;
