import React, { useState, useEffect } from 'react';
import { Document, Page } from 'react-pdf';
import * as pdfjs from 'pdfjs-dist';
import pdfjsWorker from 'pdfjs-dist/build/pdf.worker.entry';
import { Packer, Document as DocxJS, Paragraph } from 'docx';


pdfjs.GlobalWorkerOptions.workerSrc = pdfjsWorker;

const PdfViewer = ({ file }) => {
    const [numPages, setNumPages] = useState(null);
    const [pageNumber, setPageNumber] = useState(1);
    const [scale, setScale] = useState(1.5);
    const [wordBlob, setWordBlob] = useState("");

    function onDocumentLoadSuccess({ numPages }) {
        setNumPages(numPages);
    }

    // Crea un documento Word con il contenuto estratto dal PDF
    const generateDocx = async (pdfText) => {
        const doc = new DocxJS();
        doc.addSection({
            children: [new Paragraph(pdfText)],
        });
        const blob = await Packer.toBlob(doc);
        setWordBlob(blob);
    };

    // Supponendo che tu abbia una funzione per estrarre il testo dal PDF...
    const extractTextFromPdf = async (pdfFile) => {
        // Implementa la logica per estrarre il testo dal PDF...
        const text = "Estratto dal tuo PDF..."; // Questo è un esempio, sostituiscilo con il tuo codice
        generateDocx(text);
    };

    useEffect(() => {
        extractTextFromPdf(file);
        // eslint-disable-next-line
    }, [file]);

    return (
        <div className="border border-gray-300 p-4 shadow-md overflow-hidden" style={{ maxHeight: '100vh' }}>

            <div className="flex items-center justify-between mb-4 sticky top-0 z-10 bg-white p-2">
                <div>
                    <button
                        className="bg-blue-500 text-white px-4 py-2 m-2"
                        onClick={() => setScale(prevScale => prevScale + 0.1)}
                    >
                        Zoom In
                    </button>
                    <button
                        className="bg-blue-500 text-white px-4 py-2 m-2"
                        onClick={() => setScale(prevScale => Math.max(prevScale - 0.1, 0.5))}
                    >
                        Zoom Out
                    </button>
                </div>

                <p className="font-extrabold text-3xl">Page {pageNumber} of {numPages}</p>

                <div>
                    <button
                        className="bg-red-500 text-white px-4 py-2 m-2 disabled:opacity-50"
                        onClick={() => setPageNumber(prevPageNumber => Math.max(prevPageNumber - 1, 1))}
                        disabled={pageNumber <= 1}
                    >
                        Previous
                    </button>
                    <button
                        className="bg-red-500 text-white px-4 py-2 m-2 disabled:opacity-50"
                        onClick={() => setPageNumber(prevPageNumber => Math.min(prevPageNumber + 1, numPages))}
                        disabled={pageNumber >= numPages}
                    >
                        Next
                    </button>
                </div>
            </div>

            <div className="flex flex-col items-start text-black pl-8 border-black border-2 overflow-auto" style={{ maxHeight: '50vh' }}>
                <Document file={file} onLoadSuccess={onDocumentLoadSuccess}>
                    <Page pageNumber={pageNumber} scale={scale} />
                </Document>
            </div>

            {/* Divisoria nera */}
            <div className="my-4 bg-black h-1"></div>

            {wordBlob && (
                <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded shadow">
                    <a href={URL.createObjectURL(wordBlob)} download="documento.docx">
                        Vuoi scaricare il file in formato Word?
                    </a>
                </button>
            )}

            {/* si puo inserire il testo elaborato o qualsiasi altro contenuto. */}

        </div>
    );
}

export default PdfViewer;
