import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import PdfViewer from './PdfViewer/pdfViewer';
import ImmagineCurva from "../../Assets/ImmagineCurvaRoma.png";

const PdfDropZone = () => {
    const [isPdf, setIsPdf] = useState(false);
    const [fileUploaded, setFileUploaded] = useState(false);
    const [selectedPdf, setSelectedPdf] = useState(null);
    const [pageNumber, setPageNumber] = useState(1);
    const [numPages, setNumPages] = useState(null);

    const onDrop = useCallback(acceptedFiles => {
        const file = acceptedFiles[0];

        if (file && file.type !== 'application/pdf') {
            setIsPdf(true);
            setFileUploaded(false);
        } else {
            setIsPdf(false);
            setFileUploaded(true);
            setSelectedPdf(file);
        }
    }, []);

    const onDocumentLoadSuccess = ({ numPages }) => {
        setNumPages(numPages);
    };

    const { getRootProps, getInputProps, isDragActive } = useDropzone({
        onDrop,
        accept: 'application/pdf'
    });

    return (
        <>
            <div className="flex flex-col items-center relative">
                <div
                    {...getRootProps()}
                    className="min-w-[40rem] border-dashed border-4 border-gray-500 p-10 mt-10 h-60 flex flex-col justify-start items-center hover:bg-gray-100"
                    style={{ backgroundImage: `url(${ImmagineCurva})` }}
                >
                    <input {...getInputProps()} />
                    {
                        isDragActive ?
                            <p className=" text-3xl font-bold ">Trascina il file PDF qui...</p> :
                            <p className="text-white text-3xl font-bold " >MAGICA MANU ... Trascina un file In curva SUD.</p>
                    }
                </div>
                {isPdf && <p className="mt-4 text-red-600">Il file inserito non Ã¨ un PDF. Per favore inserisci un file PDF.</p>}
                {fileUploaded && !isPdf && <p className="mt-4 text-green-600">File pdf caricato correttamente</p>}
                <div className=" max-h-[50rem] overflow-auto bg-blue-400 border border-3">
                    {selectedPdf && <PdfViewer file={selectedPdf} pageNumber={pageNumber} onDocumentLoadSuccess={onDocumentLoadSuccess} />}
                </div>
                <div className="flex items-center justify-between mt-4">
                    <button
                        className="bg-red-500 text-white px-4 py-2 m-2 disabled:opacity-50 text-2xl"
                        onClick={() => setPageNumber(prevPageNumber => Math.max(prevPageNumber - 1, 1))}
                        disabled={pageNumber <= 1}
                    >
                        Previous
                    </button>
                    <button
                        className="bg-red-500 text-white px-4 py-2 m-2 disabled:opacity-50 text-2xl"
                        onClick={() => setPageNumber(prevPageNumber => Math.min(prevPageNumber + 1, numPages))}
                        disabled={pageNumber >= numPages}
                    >
                        Next
                    </button>
                </div>
            </div>
        </>
    );
}

export default PdfDropZone;
