import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';

const PdfDropZone = () => {
    const [isNotPdf, setIsNotPdf] = useState(false);

    const onDrop = useCallback((acceptedFiles, fileRejections) => {
        if (fileRejections && fileRejections.length > 0) {
            setIsNotPdf(true);
        } else {
            setIsNotPdf(false);
            console.log(acceptedFiles);
        }
    }, []);

    const { getRootProps, getInputProps, isDragActive } = useDropzone({
        onDrop,
        accept: 'application/pdf'
    });

    return (
        <div className="flex flex-col items-center">
            <div {...getRootProps()} className="border-dashed border-4 border-gray-500 p-10 mt-10 mx-auto w-1/2 h-60 flex flex-col justify-center items-center hover:bg-gray-100 bg-pink-200">
                <input {...getInputProps()} />
                {
                    isDragActive ?
                        <p>Trascina il file PDF qui...</p> :
                        <p>Trascina un file PDF qui o fai clic per selezionarlo.</p>
                }
            </div>
            {isNotPdf && <p className="mt-4 text-red-600">Il file inserito non Ã¨ un PDF. Per favore inserisci un file PDF.</p>}
        </div>
    )
}

export default PdfDropZone;
